// src/redux/store.js
import { createStore, applyMiddleware, combineReducers } from 'redux';
import thunk from 'redux-thunk'; // Correct import for thunk
import userReducer from './reducers/userReducer'; // Ensure correct path
import productReducer from './reducers/productReducer'; // Ensure correct path

const rootReducer = combineReducers({
    user: userReducer, // Make sure userReducer is included
    products: productReducer,
    // other reducers can be added here
});

const store = createStore(rootReducer, applyMiddleware(thunk));

export default store;
